export const Icons = Object.fromEntries(
  Object.entries(iconMap).map(([key, { icon: IconComponent }]) => [
    key,
    createIcon(IconComponent),
  ])
) as Record<IconName, ReturnType<typeof createIcon>>

// Get all icon categories
export const getIconCategories = () => {
  const categories = new Set<IconCategory>()
  Object.values(iconMap).forEach(({ category }) => {
    categories.add(category)
  })

  return Array.from(categories).sort()
}

// Get icons by category
export const getIconsByCategory = (category: IconCategory) => {
  return Object.entries(iconMap)
    .filter(([, { category: iconCategory }]) => iconCategory === category)
    .map(([key]) => key)
}

// Get a human-readable tooltip for an icon
export const getIconTooltip = (iconName: string): string => {
  const icon = iconMap[iconName as IconName]
  return icon?.tooltip || iconName
}

export type IconFC = React.FC<IconProps>

export type IconName = keyof typeof iconMap

export type IconProps = {
  bgColor?: string
  color?: string
  filled?: boolean
  gradient?:
    | "aurora"
    | "cosmic"
    | "ember"
    | "fire"
    | "forest"
    | "midnight"
    | "neon"
    | "ocean"
    | "royal"
    | "spring"
    | "sunset"
    | "tropical"
  label?: string
} & LucideProps &
  VariantProps<typeof iconVariants>

// Helper function to get icon for language extensions
export function getIconForLanguageExtension(language: string) {
  switch (language) {
    case "json":
      return <Icons.json />
    case "css":
      return <Icons.css className="fill-foreground" />
    case "js":
    case "jsx":
    case "ts":
    case "tsx":
    case "typescript":
      return <Icons.ts className="fill-foreground" />
    case "md":
    case "markdown":
      return <Icons.md />
    default:
      return null
  }
}
